import React, { Component } from 'react';
import { View, ScrollView, StyleSheet, Image } from 'react-native';
import Card from '../Components/Card';

import { connect } from 'react-redux';
import {createAddClubAction} from '../store/action/club'

class ListClubs extends Component {
  render() {
     const {clubReducer} = this.props;

    return (
        <View>
            {clubReducer.map(club =>(
                <Card navigation={this.props.navigation} id={club.id} key={club.id} name={club.name} pays={club.pays}/>
            ))
        }
        </View>


    );
  }
}
export default connect(
  (state)=>{return state}//映射状态
  //{addClub:createAddClubAction}//映射操作状态的方法
)(ListClubs)

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    fonts: {
      marginBottom: 8,
    },
    user: {
      flexDirection: 'row',
      marginBottom: 6,
    },
    image: {
      width: 30,
      height: 30,
      marginRight: 10,
    },
    name: {
      fontSize: 16,
      marginTop: 5,
    },
  });
