import React from 'react';
import { View,TextInput } from 'react-native';
import {Text,Button } from 'react-native-elements';
import {nanoid} from 'nanoid';
import { connect } from 'react-redux'

import {nameValidator,paysValidator} from "../core/utils"
import {createAddClubAction} from '../store/action/club'

 const AddNewClub =(props)=> {
  const [name,setName] = React.useState('Liverpool F.C.');
  const [pays,setPays] = React.useState('Angleterre');
  const nameRef = React.useRef();
  const paysRef = React.useRef();

  function inscription(){
    const newName = nameRef.current.value;
    const newPays = paysRef.current.value;
    const isNom=nameValidator(newName);
    const isPays=paysValidator(newPays);
    //setName(name=>newName);
    //setPays(pays=>newPays);
    const newClub = {id:nanoid(),name:newName,pays:newPays};

    if(isNom||isPays){
      alert('nom ou pays est vide!');
      return;
  }else{
      props.addClub(newClub);
      props.navigation.navigate('ListClubs');
  }

  }
    return(
    <View style={{
      textAlign:'center',
      flex: 1,}}>
      <Text style={{ marginBottom: 8,
      textAlign:'center'
      }} h3>
         Ajouter un nouveau club
      </Text>
      <TextInput
                label="name"
                returnKeyType="next"
                ref={nameRef}
                style={{height:50,borderColor:'gray',borderWidth:1,margin:10}}
            />
       <TextInput
                label="pays"
                returnKeyType="next"
                ref={paysRef}
                style={{height:50,borderColor:'gray',borderWidth:1,margin:10}}
            />
       <Button
                title={'submit'}
                containerStyle={{
                  width:150,
                  marginHorizontal: 50,
                  marginVertical: 10,
                  marginLeft:100,
                }}
                onPress={()=>inscription()}
              />
    </View>);
}


const mapStateToProps=(state)=>(
  {state},
  {addClub:createAddClubAction}
)


export default connect(mapStateToProps)(AddNewClub)
