import React, { Component } from 'react';
import { View, ScrollView, StyleSheet, Image } from 'react-native';
import Card from '../Components/Card';

import { connect } from 'react-redux';
import {createAddClubAction} from '../store/action/club'


export default class ListClubs extends Component {
    constructor(props){
        super(props);
        this.state={
        clubs : [
            {id:'01',name:'ParisClub',pays:'France'},
            {id:'02',name:'NewYorkClub',pays:'American'},
            {id:'03',name:'BeijingClub',pays:'Chine'},
            {id:'04',name:'BerlinClub',pays:'Allemagne'},
            {id:'05',name:'BruxelleClub',pays:'Belgique'},
        ]
    }
    }

  render() {
      const {clubs} = this.state;
    return (
        <View>
            {clubs.map(club =>(
                <Card navigation={this.props.navigation} key={club.id} name={club.name} pays={club.pays}/>
            ))
        }
        </View>


    );
  }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    fonts: {
      marginBottom: 8,
    },
    user: {
      flexDirection: 'row',
      marginBottom: 6,
    },
    image: {
      width: 30,
      height: 30,
      marginRight: 10,
    },
    name: {
      fontSize: 16,
      marginTop: 5,
    },
  });
