import React, { Component } from 'react';
import { View, ScrollView, StyleSheet, Image } from 'react-native';

import Card from '../Components/Card'


export default class ListClubs extends Component {
    constructor(props){
        super(props);
        this.state={
        clubs : [
            {id:'01',name:'ParisClub',pay:'France'},
            {id:'02',name:'NewYorkClub',pay:'American'},
            {id:'03',name:'BeijingClub',pay:'Chine'},
            {id:'04',name:'BerlinClub',pay:'Allemagne'},
            {id:'05',name:'BruxelleClub',pay:'Belgique'},
        ]
    }
    }

  render() {
      const {clubs} = this.state;
    return (
        <View>
            {clubs.map(club =>(
                <Card/>
            ))
        }
            <Card/>
        </View>


    );
  }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    fonts: {
      marginBottom: 8,
    },
    user: {
      flexDirection: 'row',
      marginBottom: 6,
    },
    image: {
      width: 30,
      height: 30,
      marginRight: 10,
    },
    name: {
      fontSize: 16,
      marginTop: 5,
    },
  });
