import React, { Component } from 'react';
import { View, StyleSheet, ScrollView, Image, FlatList } from 'react-native';
import {
  Card,
  Text,
  ListItem,
  Avatar,
  Icon,
  Badge,
  ListItemProps,
  Button,
  Switch,
  colors
} from 'react-native-elements';

import { connect } from 'react-redux';
import {createAddJouerAction} from '../store/action/jouer'

class InfoDeClubScreen extends Component {
  render() {
    const {jouerReducer} = this.props;
    return ( 
        <ScrollView>
        <View style={styles.container}>
            <Card containerStyle={{ marginTop: 15 }}>
                <Card.Title>Informations of Club</Card.Title>
                <Card.Divider />
                <Text style={styles.fonts} h2>
                Nom: ParisClub
                </Text>
                <Text style={styles.fonts} h4>
                Pays: France
                </Text>
                <Text style={styles.fonts} h4>
                Saison: 2022
                </Text>
                <Text style={styles.fonts} h4>
                Déscription: c'est un club très connu et professionnel.
                </Text>
                <Card.Divider />
                <Text style={styles.fonts} h4>
                Les jouers: 
                </Text>
                <Text style={styles.fonts}>
                <ul>
                    <li>Louis </li>
                    <li>Jacky</li>
                </ul>
                </Text>
            </Card>
        </View>
        </ScrollView>
    )
  }
}

export default connect(
  (state)=>{return state}//state
)(InfoDeClubScreen)

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    fonts: {
      marginBottom: 8,
    },
  });
