//l'état initialisé
const iniState = [
    {id:'01',name:'ParisClub',pays:'France'},
    {id:'02',name:'NewYorkClub',pays:'American'},
    {id:'03',name:'BeijingClub',pays:'Chine'},
    {id:'04',name:'BerlinClub',pays:'Allemagne'},
    {id:'05',name:'BruxelleClub',pays:'Belgique'},
]

//Reducer pour les clubs
export default function clubReducer(preState=iniState,action){
    const {type,data} = action;
    switch (type) {
        case AJOUTER_CLUB:
            return [data,...preState];
        default:
            return preState;      
    }

}
