import card from '../../image/'
//l'état initialisé
const iniState = [
    {id:'01',name:'Paris Saint-Germain',pays:'France',url:'../../image/parisClub.jepg'},
    {id:'02',name:'RC Strasbourg Alsace',pays:'France',url:'../../image/strasbourClub.jepg'},
    {id:'03',name:'Manchester United F.C.',pays:'Angleterre',url:'../../image/manchesterClub.jepg'},
    {id:'04',name:'FC Bayern Munich',pays:'Allemagne',url:'../../image/bayernClub.jepg'},
    {id:'05',name:'BruxelleClub',pays:'Belgique',url:'../../image/barceloneClub.jepg'},
]

//Reducer pour les clubs
export default function clubReducer(preState=iniState,action){
    const {type,data} = action;
    switch (type) {
        case 'AJOUTER_CLUB':
            return [data,...preState];
        default:
            return preState;      
    }

}
